var yo = require('yo-yo')
var csjs = require('csjs-inject')

module.exports = fileExplorer

function fileExplorer (options) {
  options || (options = {})
  window.files = options.files
  window.editor = options.editor
  return yo`
    <div>
      <span> ${options.title} </span>
      <ul>
      ${Object.keys(files.list()).map(filename => yo`
        <li onclick=${openfile}> ${filename} </li>
      `)}
      </ul>
    </div>
  `
  function openfile (event) {
    console.log('open file', event.target.innerText)
  }
}
